    if (file) {
      const data = new FormData();
      const filename = Date.now() + file.name;
      data.append("name", filename);
      data.append("file", file);
      newApplication.resume = filename;
      try {
        await axios.post("/upload", data);
        console.log("Resume Uploaded");
      } catch (err) { }
    }
    try {
      const res = await axios.post("/apply", newApplication);
      console.log(res);
      setMessage("Your application is successfully submitted and current status is "+res.data.status+" Please keep this application ID ( " + res.data._id + " ) for future reference");

    } catch (err) { }
  };
  return (
    <>
      <Navbar />
      <div className="page-title">Check your Application Status</div>
      <div className="page-content">
        <div className="apply-form w50p center">
          <form>
            <label for="ap-no">Application Number/ID</label>
            <input type="text" id="ap-no" name="ap-no" onChange={(e) => setApn(e.target.value)}
            />
            <button type="submit" onClick={handleSubmit}>Check Status</button>
          </form>
        </div>
      </div>
    </>
  );
};

export default CheckStatus;
