import React, { useEffect, useState } from 'react'
import AdminNav from '../../resources/components/AdminNav'
import axios from 'axios';
import { useLocation } from 'react-router-dom';

const EditApplication = () => {
    const locationn = useLocation();
    const path = locationn.pathname.split("/")[3];
    const [posts, setPosts] = useState([]);

    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [phone, setPhone] = useState("");
    const [dob, setDob] = useState("");
    const [ewb, setEwb] = useState("");
    const [exp, setExp] = useState("");
    const [eab, setEab] = useState("");
    const [salary, setSalary] = useState("");
    const [city, setCity] = useState("");
    const [jid, setJid] = useState("");
    const [file, setFile] = useState(null);

    const [message, setMessage] = useState("");

    useEffect(() => {
        const getPost = async () => {
            const res = await axios.get("/apply/edit/" + path);
            setPosts(res.data);
            setStatus(res.data.status)
        };
        getPost();
    }, [path]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        const newApplication = {
            name,
            email,
            phone,
            dob,
            ewb,
            exp,
            eab,
            salary,
            city,
            jid,
        };

        try {
            const res = await axios.post("/apply", newApplication);
            console.log(res);
            // window.location.replace("/single-post/" + res.data._id);
            setMessage("Your application is successfully submitted and current status is " + res.data.status + " Please keep this application ID ( " + res.data._id + " ) for future reference");

        } catch (err) { }
    };

    return (
        <div><AdminNav />
            <div className="page-title">View/Edit Application</div>
            <div className="page-content">
                <div className='details'>

                </div>
                <div className="apply-form">
                    <form>
                        <div className="apply-form-left">
                            <label for="name">Status</label>
                            <input
                                type="text"
                                id="status"
                                name="status"
                                value={status}
                                onChange={(e) => setStatus(e.target.value)}
                                required
                            />
                            <label for="email">Email</label>
                            <input
                                type="email"
                                id="email"
                                name="email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                required
                            />
                            <label for="phone">Mobile Number</label>
                            <input
                                type="tel"
                                id="phone"
                                name="phone"
                                value={phone}
                                onChange={(e) => setPhone(e.target.value)}
                            />
                            <label for="name">Date of Birth</label>
                            <input
                                type="date"
                                id="dob"
                                name="dob"
                                value={posts.dob}
                                onChange={(e) => setDob(e.target.value)}
                            />
                            <label for="city">Current City</label>
                            <input
                                type="text"
                                id="city"
                                name="city"
                                onChange={(e) => setCity(e.target.value)}
                            />

                        </div>
                        <div className="apply-form-right">
                            <label for="city">Ever worked here before?</label>
                            <input
                                type="text"
                                id="ewb"
                                name="ewb"
                                placeholder="Type Yes or No"
                                onChange={(e) => setEwb(e.target.value)}
                            />
                            <label for="city">Experience (if any)</label>
                            <input
                                type="text"
                                id="exp"
                                name="exp"
                                placeholder="In Months"
                                onChange={(e) => setExp(e.target.value)}
                            />
                            <label for="city">Ever applied before?</label>
                            <input
                                type="text"
                                id="eab"
                                name="eab"
                                placeholder="Type Yes or No"
                                onChange={(e) => setEab(e.target.value)}
                            />
                            <label for="jid">Job ID</label>
                            <input
                                type="text"
                                id="jid"
                                name="jid"
                                onChange={(e) => setJid(e.target.value)}
                            />
                            <label for="jid">Expected Salary</label>
                            <input
                                type="text"
                                id="salary"
                                name="salary"
                                placeholder="INR"
                                onChange={(e) => setSalary(e.target.value)}
                            />

                        </div>
                        <button type="submit" onClick={handleSubmit}>
                            Apply &nbsp;<i class="fa fa-external-link" aria-hidden="true"></i>
                        </button>
                    </form>


                </div>
                <div className="message-box">
                    {message && <p>{message}</p>}
                </div>
            </div></div>
    )
}

export default EditApplication
